#include <iostream>
using namespace std;

int main() {
    int n;
    long long factorial = 1;
    cin >> n;
    if (n < 0) return 1;
    for(int i = 1; i <= n; ++i) {
        factorial *= i;
    }
    cout << factorial;
    return 0;
}

