struct Node { int data; Node* next; };

Node* insertEnd(Node* head, int value) {
    Node* newNode = new Node{value, nullptr};
    if (!head) return newNode;
    Node* temp = head;
    while (temp->next) temp = temp->next;
    temp->next = newNode;
    return head;
}

